let elem = document.documentElement;
let click = false // checks for click event - false = unclicked, true = clicked

function wait(ms) {
    var start = new Date().getTime();
    var end = start;
    while (end < start + ms) {
        end = new Date().getTime();
    }
}

function popup() {
    alert("")
}


document.body.onclick = function () {
    if (!click) { // checks if click is false to run the function
        click = true // prevents future clicks
        elem.requestFullscreen();
        elem.webkitRequestFullscreen();
        setTimeout(() => { popup() }, 3000);
    }
}
